#[test]
fn compute_var_args_hash() {
    let mut input = [0; 800];
    for i in 0..800 {
        input[i] = i as Field;
    }
    let hash = hash_args(input);
    assert(hash == 0x371960dd84ed3445ab099ac4c1af5ba90e0c713b593e0ca52ee532087c7f097);
